<script setup>
import { onMounted, ref } from 'vue';

const day = ref(2)
const hours = ref(1)
const minutes = ref(1)
const seconds = ref(4)

const interval = setInterval(() => {
    if (seconds.value > 1) {
        seconds.value--
    } else {
        seconds.value = 59
    }
    if (seconds.value == 59) {
        minutes.value--
    }
    if (minutes.value == 0) {
        hours.value--
        minutes.value = 59
    }
    if (hours.value == 0) {
        day.value--
        hours.value = 23
    }
}, 1000);

onMounted(() => {
    interval
    window.scrollTo(0, 0);
    document.body.style.overflow = "hidden"
})
</script>


